import React, { useEffect, useState } from "react";
import { apiGet } from "../utils/api";
import { useParams } from "react-router-dom";
import dateStringFormatter from "../utils/dateStringFormatter";
import { CZMoneyFormat } from "../utils/CZMoneyFormatter";

const InvoiceDetail = () => {
    const {id} = useParams();
    const [invoice, setInvoice] = useState({});

    useEffect(() => {
        apiGet("/api/invoices/" + id).then((data) => setInvoice(data))
    }, [id]);

    return (
        <>
            <div>
                <h1>Detail faktury</h1>
                <hr/>
                <h3>Číslo faktury: {invoice.invoiceNumber}</h3>
                <p>
                    <strong>Datum vydání:</strong>
                    <br></br>
                    {dateStringFormatter(invoice.issued)}
                </p>
                <p>
                    <strong>Datum splatnosti:</strong>
                    <br></br>
                    {dateStringFormatter(invoice.dueDate)}
                </p>
                <p>
                    <strong>Produkt:</strong>
                    <br></br>
                    {CZMoneyFormat.format(invoice.product)}
                </p>
                <p>
                    <strong>Cena:</strong>
                    <br></br>
                    {invoice.price}
                </p>
                <p>
                    <strong>Daň:</strong>
                    <br></br>
                    {invoice.vat}
                </p>
                <p>
                    <strong>Poznámka:</strong>
                    <br></br>
                    {invoice.note}
                </p>
            </div>
        </>
    );
};

export default InvoiceDetail;
