import {useEffect, useState} from "react";
import {apiGet} from "../utils/api";
import PersonStatisticsTable from "./PersonStatisticsTable";


const StatisticsIndex = () => {
    const [generalStatisticState, setGeneralStatictic] = useState({});
    const [personStattisticsState, setPersonStatistics] = useState([]);

    useEffect(() => {
        apiGet("/api/invoices/statistics").then((data) => setGeneralStatictic(data));
        apiGet("/api/persons/statistics").then((data) => setPersonStatistics(data));
    }, []);

    return (
        <>
            <h1>Výpis obecných statitik</h1>
            <table className="table table-bordered">
                <thead>
                    <tr>
                        <th>Součet příjmá za letošní rok</th>
                        <th>Součet všech příjmů</th>
                        <th>Počet faktur</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>{generalStatisticState.currentYearSum}</td>
                        <td>{generalStatisticState.allTimeSum}</td>
                        <td>{generalStatisticState.invoicesCount}</td>
                    </tr>
                </tbody>
            </table>
            <h1>Výpis statistik pro jednotlivé společnosti</h1>
            <PersonStatisticsTable items={personStattisticsState}></PersonStatisticsTable>
        </>
    );
};

export default StatisticsIndex;
